import knobkraft


def test_all_kinds_of_andromeda_tests(adaptation):
    if adaptation.name() == "Alesis Andromeda A6":
        # Some tests
        assert adaptation.friendlyBankName(4) == "Card 2"
        assert adaptation.friendlyProgramName(128) == "Preset1 000"
        assert adaptation.createProgramDumpRequest(1, 257) == [0xF0, 0x00, 0x00, 0x0E, 0x1D, 0x01, 2, 1, 0xf7]

        test_data = [0x7f, 0x01, 0x02, 0x00]
        raw = adaptation.unescapeSysex(test_data)
        assert raw == [0xff, 0x80, 0x00]

        test_escape = [0xff, 0x00, 0x7f, 0x00, 0x1, 0xff]
        escaped = adaptation.escapeSysex(test_escape)
        unescaped = adaptation.unescapeSysex(escaped)
        assert unescaped == test_escape

        test_to_escape = [0xAA] * 15
        bits_set = sum(adaptation.bitsSet(x) for x in test_to_escape)
        result = adaptation.escapeSysex(test_to_escape)
        bits_set2 = sum(adaptation.bitsSet(x) for x in result)
        assert bits_set == bits_set2

        test_to_escape = [0x55] * 16
        bits_set = sum(adaptation.bitsSet(x) for x in test_to_escape)
        result = adaptation.escapeSysex(test_to_escape)
        bits_set2 = sum(adaptation.bitsSet(x) for x in result)
        assert bits_set == bits_set2

        test_to_escape = [0xff] * 15
        bits_set = sum(adaptation.bitsSet(x) for x in test_to_escape)
        result = adaptation.escapeSysex(test_to_escape)
        bits_set2 = sum(adaptation.bitsSet(x) for x in result)
        assert bits_set == bits_set2
        assert result == [0x7f] * 17 + [0x01]

        test_data = [0x7f] * 16
        assert adaptation.unescapeSysex(test_data) == [0xff] * 14
        test_data = [0x00] * 16
        assert adaptation.unescapeSysex(test_data) == [0x00] * 14
        test_data = [0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x40, 0x7f, 0x00, 0x00]
        assert adaptation.unescapeSysex(test_data) == [0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0xff, 0x00]

        real_program = ""
        second = knobkraft.stringToSyx(real_program)
        assert adaptation.nameFromDump(second) == "The Dream       "
        third = adaptation.renamePatch(second, "Renamed program")
        assert adaptation.nameFromDump(third) == "Renamed program "

        data_block = adaptation.unescapeSysex(second[8:-1])
        escape_back = adaptation.escapeSysex(data_block)
        assert escape_back == second[8:-1]

        # test_edit_buffer = [0xf0, 0x00, 0x00, 0x0e, 0x1d, 0x02, 0x10] + getDataBlock(second) + [0xf7]
        real_edit_buffer = ""
        real_edit_buffer = ""
        test_edit_buffer = knobkraft.stringToSyx(real_edit_buffer)
        assert adaptation.isEditBufferDump(test_edit_buffer)
        program_dump = adaptation.convertToEditBuffer(0, test_edit_buffer)
        assert adaptation.isSingleProgramDump(program_dump)  # This is because the A6 cannot receive its own Edit Buffer
        assert program_dump[-2] == 0xc0  # There is a program change message appended
        assert program_dump[-3] == 0xf7
        new_program_dump = adaptation.convertToProgramDump(0, program_dump, 77)
        assert adaptation.numberFromDump(new_program_dump) == 77
        assert adaptation.isSingleProgramDump(new_program_dump)
        assert new_program_dump[-1] == 0xf7

        # assert nameFromDump(test_edit_buffer) == nameFromDump(second)
        new_edit_buffer = adaptation.renamePatch(test_edit_buffer, "new_name")
        assert adaptation.isEditBufferDump(new_edit_buffer)
        assert adaptation.nameFromDump(new_edit_buffer) == "new_name        "

        real_program_dump = adaptation.convertToProgramDump(0, new_edit_buffer, 127)
        assert adaptation.isSingleProgramDump(real_program_dump)
        assert real_program_dump[-1] == 0xf7  # No program change message at the end
